import React, {FC, useState} from 'react';
import {Button} from "@material-ui/core";
import * as web3 from '@solana/web3.js'; 

import "./style.scss";

const KeyGenerator: FC = () => {
  const [goalWord, setGoalWord] = useState("ab");
  const [publicKey, setPublicKey] = useState("calculating...");
  const [privateKey, setPrivateKey] = useState("calculating...");
  const [caseSensitive, setCaseSensitive] = useState(false); 

  const handleChange = (e) => {
    setGoalWord(e.target.value); 
  };

  const handleCaseSensitiveChange = (e) => {
    setCaseSensitive(e.target.checked); 
  }

  const handleSubmit = () => {
    getKeys(); 
  };

  function makeid(length) {
    var result           = '';
    var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    var charactersLength = characters.length;
    for ( var i = 0; i < length; i++ ) {
      result += characters.charAt(Math.floor(Math.random() * 
      charactersLength));
    }
    return result;
  }

  const getKeys = () => {
    console.log(goalWord);
    var keyString = makeid(goalWord.length)
    var key =  web3.Keypair.generate();

    if (!caseSensitive) {
      while (keyString.slice(0, goalWord.length).toLowerCase() != goalWord) {
        key =  web3.Keypair.generate();
        keyString = key.publicKey.toString();
      }
    } else {
      while (keyString.slice(0, goalWord.length) != goalWord) {
        key =  web3.Keypair.generate();
        keyString = key.publicKey.toString();
      }
    }

    setPublicKey(keyString);
    setPrivateKey(key); 
  }

  return (
    <div className="airdrop-container">
      <h3>Generate a keypair for which the public key starts with letters of your choice ðŸŽ‰</h3>
      <div className="airdrop-wrapper">
        <p className="red">All code runs in your browser; no-one, except for you has access to the generated keypair. Feel free to fork on Github and run locally.</p>
        <h3>Goal word</h3>
        <p>Enter the word your public key should start with (the longer the word, the longer the wait).</p>
        <input
          name="goalWord"
          type="text"
          value={goalWord}
          onChange={handleChange}
          className="airdrop-pk-input stake-input borrower-pk credix-button MuiButtonBase-root MuiButton-root MuiButton-contained MuiButton-containedPrimary balance-button"
        />
        <label>
          Case sensitive:
          <input
            name="caseSensitive"
            type="checkbox"
            checked={caseSensitive}
            onChange={handleCaseSensitiveChange} />
        </label>
        <Button
            variant="contained"
            className="stake-submit MuiButton-containedPrimary balance-button credix-button sol-airdrop"
            onClick={handleSubmit}
        >
         GET KEY
        </Button>
        <h3>Public Key:</h3>
        <p>{publicKey}</p>
        <h3>Private key:</h3>
        <button
            href={`data:text/json;charset=utf-8,${encodeURIComponent(
              JSON.stringify(privateKey)
            )}`}
            download="id.json"
          >
            {`Download Json`}
          </button>
       </div>
    </div>
  );
};

export default KeyGenerator;
